\begin {tikzcd}
	Fc && Gc \\
	\\
	{Fc'} && {Gc'}
	\arrow ["{\alpha _c}", from=1-1, to=1-3]
	\arrow ["Ff"', from=1-1, to=3-1]
	\arrow ["Gf", from=1-3, to=3-3]
	\arrow ["{\alpha _{c'}}"', from=3-1, to=3-3]
\end {tikzcd}

\begin {tikzcd}[row sep=tiny]
	{\mathsf {C}} & {\mathsf {Set}} \\
	{f\colon  x\rightarrow  y} & {f_*\colon  \mathsf {C}(c,x)\rightarrow  \mathsf {C}(c,y)}
	\arrow [shorten=5mm, "{\mathsf {C}(c,-)}", from=1-1, to=1-2]
	\arrow [maps to, from=2-1, to=2-2]
\end {tikzcd}

\begin {tikzcd}[row sep=tiny]
	{\mathsf {C}^{op}} & {\mathsf {Set}} \\
	{f\colon  x\rightarrow  y} & {f^*\colon  \mathsf {C}(x,c)\rightarrow  \mathsf {C}(y,c)}
	\arrow [shorten=5mm, "{\mathsf {C}(-,x)}", from=1-1, to=1-2]
	\arrow [maps to, from=2-2, to=2-1]
\end {tikzcd}

\begin {tikzcd}[row sep=tiny]
	{\mathsf {C}} & {\mathsf {Set}} \\
	{f\colon  x\rightarrow  y} & {f_*\colon  \mathsf {C}(c,x)\rightarrow  \mathsf {C}(c,y)}
	\arrow [shorten=5mm, "{\mathsf {C}(c,-)}", from=1-1, to=1-2]
	\arrow [maps to, from=2-1, to=2-2]
\end {tikzcd}

\begin {tikzcd}[row sep=tiny]
	{\mathsf {C}^{op}} & {\mathsf {Set}} \\
	{f\colon  x\rightarrow  y} & {f^*\colon  \mathsf {C}(x,c)\rightarrow  \mathsf {C}(y,c)}
	\arrow [shorten=5mm, "{\mathsf {C}(-,x)}", from=1-1, to=1-2]
	\arrow [maps to, from=2-2, to=2-1]
\end {tikzcd}

